import React from 'react';
import './Card.css';

const PromotionCard = ({ promotion }) => (
  <div className="promotion-card">
    <img src={promotion.imageUrl} alt="Url da Imagem" className="promotion-card__image"/>
    <div className="promotion-card__info">
      <h1 className="promotion-card__title">{promotion.title}</h1>
      <span className="promotion-card__price">{promotion.price}</span>
      <footer className="promotion-card__footer">
        {promotion.comments.length > 0 && (
          <div className="promotion-card__comment">"{promotion.comments[0].comment}"</div>
        )}

        <div className="promotion-card__comments-count">
          {promotion.comments.length} 
          {promotion.comments.length > 1 ? 'Coment치rios' : 'Coment치rio'}
          </div>
        <a href={promotion.url} target="_blank" className="promotion-card__link" rel="noreferrer">IR PARA O SITE</a>
      </footer>
    </div>
  </div>
)

export default PromotionCard;
